import styled from "styled-components";

const Wrapper = styled.section`
 margin: 3rem 0 0rem;
 wigth: 100%;
 display: grid;
 grid-template-column: 100%;
 gap: 2rem;
 `;

const InfoImage = styled.img`
  display: block;
  width: 100%;
  height: 100%;
  object-fit: contain;
  box-shadow: var(--shadow);
`;

const InfoTitle = styled.h1`
  font-size: var(--fs-md);
  font-weight: var(--fw-bold);
  margin: 3rem 0 2rem;
`;

const ListGroup = styled.div``;

const List = styled.ul`
  list-style: none;
  margin: 0 0 2rem;
  padding: 0;
`;

const ListItem = styled.li`
  font-size: var(--fs-sm);
  & > b {
    font-weight: var(--fw-bold);
  }
`;

const Meta = styled.div`

  & > b {
    font-weight: var(--fw-bold);
  }
`;

const TagGroup = styled.div``;

const Tag = styled.span`
  padding: 0 1rem;
  line-height: 1.5;
  gap: 1rem;
  background-color: var(--colors-ui-base);
`;



export const DetailsInfo = (props) => {
  const {
    flag,
    name,
    nativeName,
    population,
    region,
    subregion,
    capital,
    topLevelDomain = [],
    currencies = [],
    languages = [],
    borders = [],
  } = props;

  console.log('props', props);
  
  
  return (
    <Wrapper>
      <InfoImage src={flag} alt='flag' />

      <div>
        <InfoTitle>{name}</InfoTitle>
        <ListGroup>
          <List>
            <ListItem>
              <b>Native Name:</b> {nativeName}
            </ListItem>
            <ListItem>
              <b>Population:</b> {population}
            </ListItem>
            <ListItem>
              <b>Region:</b> {region}
            </ListItem>
            <ListItem>
              <b>Sub Region:</b> {subregion}
            </ListItem>
            <ListItem>
              <b>Capital:</b> {capital}
            </ListItem>
          </List>

          <List>
            <ListItem>
              <b>Top Level Domain:</b> {
                topLevelDomain.map(item => (
                  item
              ))};
            </ListItem>
            <ListItem>
              <b>Currencies:</b> {
                currencies.map(item => (
                  <span key={item.code}>{item.name}</span>
              ))};
            </ListItem>
            <ListItem>
              <b>Languages:</b> {
                languages.map(item => (
                  <span key={item.name}>{item.name}</span>
              ))};
            </ListItem>
          </List>
        </ListGroup>
        <Meta>
          <b>Border Countries:</b>
            {!borders.length ? (
              <span>There is no border</span>
            ) : (
              <TagGroup>
                {borders.map(item => (
                  <Tag key={item}>{item}</Tag>
                ))}
              </TagGroup>
            )}
        </Meta>
      </div>
    </Wrapper>
  )
}
