import axios from 'axios';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import { List } from '../components/List';
import { Controls } from '../components/Controls';
import { Card } from '../components/Card';
import { ALL_COUNTRIES } from '../config';

export const HomePage = ( {countries, setCountries} ) => {

  const [filteredCountries, setFilteredCountries] = useState(countries);

console.log('filteredCountries >>>', filteredCountries);

  const handleSearch = ((search, region) => {
    let data = [...countries];
    console.log()

    if(region) {
      data = data.filter(item => item.region.includes("As"))
      // data = data.filter(item => item.region === region.value)
    }
    // console.log('data >>>', data);

    setFilteredCountries(data);
  })

  const navigate = useNavigate();

  useEffect(() => {
    axios.get(ALL_COUNTRIES).then(
      ({data}) => setCountries(data)
    )
  }, []);

  return (
    <div>
      <>
      <Controls handleSearch={handleSearch}/>
        <List>
          {
            countries.sort((a, b) => a.name.official > b.name.official ? 1 : -1).map(c => {
              const countryInfo = {
                img: c.flags.png,
                name: c.name.official,
                info: [
                  {
                    title: 'Population',
                    description: c.population.toLocaleString(),
                  },
                  {
                    title: 'Region',
                    description: c.region,
                  },
                  {
                    title: 'Capital',
                    description: c.capital,
                  },
                ],
              };

              return <Card key={c.name.common} onClick={() => navigate(`/country/${c.name.official}`)} {...countryInfo}/>
            })
          }
        </List>
      </>
    </div>
  )
}
